mapper.select:
SELECT ACCG_MODL_CD
     , COHT_YEAR
     , PRTP_CD
     , ERPF_GRP_CD
     , 'YYYY-MM-DD' AS CREATED_DATE
     , CREATED_BY
  FROM TB_GOCINFO
 WHERE 1=1

   AND ACCG_MODL_CD = #{accgModlCd}


   AND COHT_YEAR = #{cohtYear}


   AND PRTP_CD = #{prtpCd}


   AND ERPF_GRP_CD = #{erpfGrpCd}
