mapper.select:
SELECT ROW_NUMBER() OVER(ORDER BY (SELECT 1)) AS ROW_NUMBER , STD_YYMM , INSC_DV_CD , VALU_METD_CD , PTF_CD , BAS_AMT , OCI_AMT , NEW_CONT_AMT , INT_EFFT_AMT , ESTM_PRM , ESTM_FEE , ESTM_NEW_CONT_CCLS_AMT , ESTM_FEE_RFND_AMT , ESTM_CONT_MTN_AMT , ESTM_IVSG_EXP_AMT , ATS_ASSM_UPD_AMT , EXRT_EFFT_AMT , ENTM_AMT , FRST_REG_EMP_NO , FRST_REG_DTM FROM TB_INSU_PL_TTL_LST WHERE 1=1 AND INSU_PL_CHNG_DV_CD = 'RA'
AND CS_NO = #{csNo}
AND ( STD_YYMM LIKE '%' + #{stdYymm} + '%' )
