mapper.select:
SELECT ROW_NUMBER() OVER(ORDER BY (SELECT 1)) AS ROW_NUMBER , ATIT_CD , ATIT_KOR_NM , ATIT_ENG_NM , HGRK_ATIT_CD , AC_LVL_CD , BKG_AC_YN , FS_DV_CD , AC_DV_CD , AC_KIND_CD , APP_ST_DT , APP_ED_DT , ORD_SEQ , ETC_DESC , USE_YN , FRST_REG_EMP_NO , FRST_REG_DTM FROM TB_ATIT_MNG_LST WHERE 1=1
AND CS_NO = #{csNo}
AND ( ATIT_CD LIKE '%' + #{searchTxt} + '%' OR ATIT_KOR_NM LIKE '%' + #{searchTxt} + '%' OR ATIT_ENG_NM LIKE '%' + #{searchTxt} + '%' )

mapper.insert:
		INSERT INTO [dbo].[TB_ATIT_MNG_LST] (
			  [CS_NO]
			, [ATIT_CD]
			, [ATIT_KOR_NM]
			, [ATIT_ENG_NM]
			, [HGRK_ATIT_CD]
			, [AC_LVL_CD]
			, [BKG_AC_YN]
			, [FS_DV_CD]
			, [AC_DV_CD]
			, [AC_KIND_CD]
			, [APP_ST_DT]
			, [APP_ED_DT]
			, [ORD_SEQ]
			, [ETC_DESC]
			, [USE_YN]
			, [FRST_REG_EMP_NO]
		    , [FRST_REG_DTM]
			, [FNL_UPD_EMP_NO]
			, [FNL_UPD_DTM]
        )
        VALUES (
			  #{csNo}
			, #{atitCd}
            , #{atitKorNm}
            , #{atitEngNm}
            , #{hgrkAtitCd}
            , #{acLvlCd}
            , #{bkgAcYn}
            , #{fsDvCd}
            , #{acDvCd}
            , #{acKindCd}
            , #{appStDt}
            , #{appEdDt}
            , #{ordSeq}
            , #{etcDesc}
            , #{useYn}
			, #{frstRegEmpNo}
            , CONVERT(CHAR(10), getdate(), 23)
            , #{frstRegEmpNo}
            , CONVERT(CHAR(10), getdate(), 23)
        )

mapper.delete:
DELETE FROM TB_ATIT_MNG_LST
WHERE 1=1
  AND ATIT_CD = #{atitCd}
  AND CS_NO   = #{csNo}
