mapper.select:
SELECT ROW_NUMBER() OVER(ORDER BY (SELECT 1)) AS ROW_NUMBER , A.ATIT_CD , A.ATIT_KOR_NM , A.ATIT_ENG_NM , A.HGRK_ATIT_CD , A.AC_LVL_CD , A.BKG_AC_YN , A.FS_DV_CD , A.AC_DV_CD , A.AC_KIND_CD , A.APP_ST_DT , A.APP_ED_DT , A.ORD_SEQ , A.ETC_DESC , A.USE_YN , B.USR_NM AS FRST_REG_EMP_NO , A.FRST_REG_DTM FROM TB_ATIT_MNG_LST A LEFT OUTER JOIN TB_USR_MNG_LST B ON A.FNL_UPD_EMP_NO = B.USR_ID WHERE 1=1
AND A.CS_NO = #{csNo}
AND ( A.ATIT_CD LIKE '%' + #{searchTxt} + '%' OR A.ATIT_KOR_NM LIKE '%' + #{searchTxt} + '%' OR A.ATIT_ENG_NM LIKE '%' + #{searchTxt} + '%' )

mapper.insert:
		INSERT INTO [dbo].[TB_ATIT_MNG_LST] (
			  [CS_NO]
			, [ATIT_CD]
			, [ATIT_KOR_NM]
			, [ATIT_ENG_NM]
			, [HGRK_ATIT_CD]
			, [AC_LVL_CD]
			, [BKG_AC_YN]
			, [FS_DV_CD]
			, [AC_DV_CD]
			, [AC_KIND_CD]
			, [APP_ST_DT]
			, [APP_ED_DT]
			, [ORD_SEQ]
			, [ETC_DESC]
			, [USE_YN]
			, [FRST_REG_EMP_NO]
		    , [FRST_REG_DTM]
			, [FNL_UPD_EMP_NO]
			, [FNL_UPD_DTM]
        )
        VALUES (
			  #{csNo}
			, #{atitCd}
            , #{atitKorNm}
            , #{atitEngNm}
            , #{hgrkAtitCd}
            , #{acLvlCd}
            , #{bkgAcYn}
            , #{fsDvCd}
            , #{acDvCd}
            , #{acKindCd}
            , #{appStDt}
            , #{appEdDt}
            , #{ordSeq}
            , #{etcDesc}
            , #{useYn}
			, #{frstRegEmpNo}
            , CONVERT(CHAR(10), getdate(), 23)
            , #{frstRegEmpNo}
            , CONVERT(CHAR(10), getdate(), 23)
        )

mapper.delete:
DELETE FROM TB_ATIT_MNG_LST
WHERE 1=1
  AND ATIT_CD = #{atitCd}
  AND CS_NO   = #{csNo}
