mapper.select:
SELECT ROW_NUMBER() OVER(ORDER BY (SELECT 1)) AS ROW_NUMBER , AA.CS_NO AS CS_NO , AA.INSC_DV_CD AS INSC_DV_CD , AA.VALU_METD_CD AS VALU_METD_CD , AA.LVL_1_CD AS LVL_1_CD , AA.LVL_2_CD AS LVL_2_CD , AA.LVL_3_CD AS LVL_3_CD , AA.DR_AC_SUBJ_CD AS DR_AC_SUBJ_CD , AA.ATIT_ENG_NM AS DR_AC_SUBJ_NM , AA.CR_AC_SUBJ_CD AS CR_AC_SUBJ_CD , BB.ATIT_ENG_NM AS CR_AC_SUBJ_NM , AA.ETC_DESC AS ETC_DESC , AA.USE_YN AS USE_YN , AA.FRST_REG_EMP_NO AS FRST_REG_EMP_NO , AA.FRST_REG_DTM AS FRST_REG_DTM FROM ( SELECT A.CS_NO AS CS_NO , A.INSC_DV_CD AS INSC_DV_CD , A.VALU_METD_CD AS VALU_METD_CD , A.LVL_1_CD AS LVL_1_CD , A.LVL_2_CD AS LVL_2_CD , A.LVL_3_CD AS LVL_3_CD , A.DR_AC_SUBJ_CD AS DR_AC_SUBJ_CD , B.ATIT_ENG_NM AS ATIT_ENG_NM , A.CR_AC_SUBJ_CD AS CR_AC_SUBJ_CD , A.ETC_DESC AS ETC_DESC , A.USE_YN AS USE_YN , A.FRST_REG_EMP_NO AS FRST_REG_EMP_NO , A.FRST_REG_DTM AS FRST_REG_DTM FROM TB_JNL_RUL_MNG_LST AS A INNER JOIN TB_ATIT_MNG_LST AS B ON A.DR_AC_SUBJ_CD = B.ATIT_CD ) AA INNER JOIN TB_ATIT_MNG_LST AS BB ON AA.CR_AC_SUBJ_CD = BB.ATIT_CD WHERE 1=1
AND AA.CS_NO = #{csNo}
AND ( DR_AC_SUBJ_CD LIKE '%' + #{searchTxt} + '%' OR AA.ATIT_ENG_NM LIKE '%' + #{searchTxt} + '%' OR CR_AC_SUBJ_CD LIKE '%' + #{searchTxt} + '%' OR AA.ATIT_ENG_NM LIKE '%' + #{searchTxt} + '%' )

mapper.insert:
INSERT INTO [dbo].[TB_JNL_RUL_MNG_LST] 
	(
	  [CS_NO]
	, [INSC_DV_CD]
	, [VALU_METD_CD]
	, [LVL_1_CD]
	, [LVL_2_CD]
	, [LVL_3_CD]
	, [DR_AC_SUBJ_CD]
	, [CR_AC_SUBJ_CD]
	, [ETC_DESC]
	, [USE_YN]
	, [FRST_REG_EMP_NO]
	, [FRST_REG_DTM]
	, [FNL_UPD_EMP_NO]
	, [FNL_UPD_DTM]
	)
VALUES (
		  #{csNo}
		, #{inscDvCd}
		, #{valuMetdCd}
		, #{lvl1Cd}
		, #{lvl2Cd}
		, #{lvl3Cd}
		, #{drAcSubjCd}
		, #{crAcSubjCd}
		, #{etcDesc}
		, #{useYn}
		, #{frstRegEmpNo}
		, CONVERT(CHAR(10), getdate(), 23)
		, #{frstRegEmpNo}
		, CONVERT(CHAR(10), getdate(), 23)
		)

mapper.update:
UPDATE TB_JNL_RUL_MNG_LST
   SET
       DR_AC_SUBJ_CD  = #{drAcSubjCd},
       CR_AC_SUBJ_CD  = #{crAcSubjCd},
       ETC_DESC       = #{etcDesc},
       USE_YN         = #{useYn},
       FNL_UPD_EMP_NO = #{frstRegEmpNo},
       FNL_UPD_DTM    = CONVERT(CHAR(10), getdate(), 23)
  WHERE 1=1
    AND CS_NO        = #{csNo}
    AND INSC_DV_CD   = #{inscDvCd}
    AND VALU_METD_CD = #{valuMetdCd}
    AND LVL_1_CD     = #{lvl1Cd}
    AND LVL_2_CD     = #{lvl2Cd}
    AND LVL_3_CD     = #{lvl3Cd}

mapper.delete:
DELETE FROM TB_JNL_RUL_MNG_LST
 WHERE 1=1
   AND CS_NO        = #{csNo}
   AND INSC_DV_CD   = #{inscDvCd}
   AND VALU_METD_CD = #{valuMetdCd}
   AND LVL_1_CD     = #{lvl1Cd}
   AND LVL_2_CD     = #{lvl2Cd}
   AND LVL_3_CD     = #{lvl3Cd}
