mapper.select:
SELECT ROW_NUMBER() OVER(ORDER BY (SELECT 1)) AS rowNumber /* 행번호 */ , A.BAT_EXE_ID AS batExeId /* 배치실행ID */ , A.BAT_PROG_ID AS batProgId /* 배치프로그램ID */ , B.BAT_PROG_NM AS batProgNm /* 배치프로그램명 */ , A.BAT_EXE_RST_CD AS batExeRstCd /* 배치실행결과코드 */ , A.BAT_EXE_ERR_CD AS batExeErrCd /* 배치실행에러코드 */ , A.STD_YYMM AS stdYymm /* 기준년월 */ , C.USR_NM AS mainEmpNo /* 주담당자 */ , C.USR_NM AS frstRegEmpNo /* 최초등록사원번호 */ , A.FRST_REG_DTM AS frstRegDtm /* 최초등록일시 */ FROM TB_BAT_RST_LST AS A INNER JOIN TB_BAT_MNG_LST AS B ON A.BAT_PROG_ID = B.BAT_PROG_ID LEFT OUTER JOIN TB_USR_MNG_LST C ON A.FNL_UPD_EMP_NO = C.USR_ID WHERE 1=1
AND A.CS_NO = #{csNo}
AND ( A.BAT_PROG_ID LIKE '%' + #{searchTxt} + '%' )
AND A.STD_YYMM = #{stdYymm}
