mapper.select:
SELECT A.TBL_ID AS tblId /* 테이블ID */ , A.CLMN_ID AS clmnId /* 컬럼ID */ , A.ITM_NM AS itmNm /* 칼럼명 */ , A.ORD_SEQ AS ordSeq /* 정렬순서 */ , A.PK_YN AS pkYn /* PK여부 */ , A.DATA_TP_CD AS dataTpCd /* 데이터타입ID */ , A.ITM_DSC AS itmDsc /* 항목설명 */ , A.VLVL_VRF_YN AS vlvlVrfYn /* 유효값검증여부 */ , A.VLVL_CD AS vlvlCd /* 유효값코드 */ , A.SEL_BAS_YN AS selBasYn /* 조회기준여부 */ , B.DB_ID AS dbId /* 데이터베이스ID */ , B.UI_SEL_YN AS uiSelYn /* 화면조회여부 */ FROM RDSDB.dbo.TB_UPL_ITM_LST A INNER JOIN RDSDB.dbo.TB_UPL_FILE_LST B ON A.TBL_ID = B.TBL_ID
AND B.SEQ_NO = #{uploadFile}
WHERE 1=1 ORDER BY A.ORD_SEQ

mapper.update:
UPDATE RDSDB.dbo.TB_UPL_HIST
   SET FILE_NM        = #{fileNm}
     , LOAD_END_DTM     = GETDATE()
     , LOAD_CNT       = #{loadCnt}
     , LOAD_SUCC_YN   = #{loadSuccYn}
     , STD_YYMM       = #{stdYymm}
     , FNL_UPD_EMP_NO = #{usrId}
     , FNL_UPD_DTM    = GETDATE()
 WHERE 1=1
   AND SEQ_NO = #{seqNo}
